

const sli=(ary, para=3)=>{
  const seri=Math.floor(
    ary.length/para);
  const rest=ary.length%para;

  console.log(seri*para, rest);
  console.log(ary.slice(seri * para));
  console.log(ary.slice(seri * para, seri * para * 2));
};


sli(['a','b','c','d','e','f']);
sli(['a','b','c','d','e','f','g','h']);
