
module.exports=class {

  template(){
    return '';
  }

  props(opt){ return opt; }
  render(opt){
    const props=this.props(opt);
    const template=this.template(opt);

    return template
      .split('\n')
    .map( l => {
      const s=l.split(/{{%|%}}/);
  
      return 1 < s.length
        ? s.map(
            (s,i) => 0 < i%2
              ? props[s.replace(/^ */,'')
                   .replace(/ *$/,'')]
              : s
          ).join('')
        : s;
    }).join('\n');
  }

}
