#!/bin/bash

dir=$(dirname $0)
  mkdir -p $dir/log
js=communicate.html.sample.js


for pid in $(
  ps aux \
  |grep -v grep \
  |grep $js \
  |awk '{print $2}'
);do
  echo kill $pid
  kill $pid \
    && rm -rf /tmp/submarine-*
done

node $dir/$js \
  > $dir/log/stdout.log \
  2> $dir/log/stderr.log \
&

