const exec=require('child_process').exec;
const fs=require('fs');



fs.readFile(`${__dirname}/file.txt`, (err, buf)=>{
  const data=buf.toString()
    .replace(/\n$/, '');
  exec(`echo "${data}" > ${__dirname}/file2.txt`, (err, stdout, stderr)=>{
    console.log(err, stdout, stderr);
  });
});

