require('Extended')

const crypto=require('crypto');


const hex=Array.seq(256, (i)=>{
  return (i + 0x100)
    .toString(16)
    .substr(1);
});
const rand=crypto.randomBytes(4);
  
/*console.log(
  hex[hex.length-1]
);*/
console.log(
  rand.toString('hex')
);
console.log(
  rand.map( r => r ).toString('hex')
);
console.log(
  Array.from(rand).map( r => hex[r] ).join('')
);
console.log(
  [
    hex[rand[0]], 
    hex[rand[1]], 
    hex[rand[2]], 
    hex[rand[3]],
  ].join('')
);

